def searchBar(input_id,base_color='2c2e31',default_text='',data_id=''):
    sc = f"<script>searchAttach(document.getElementById('{input_id}'), document.getElementById('{data_id}'));</script>" if data_id else ''
    return f'<div style="display:flex !important;margin:.25rem .5rem .25rem .5rem;box-sizing:border-box;align-items:center;position:relative;margin-bottom:.25rem;margin-top:.25rem;margin-left:.5rem;margin-right:.5rem;line-height:1.15;"><svg style="color:#{base_color};height:1.25rem;left:.75rem;opacity:.6;position:absolute;width:1.25rem;z-index:1;" xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="none" viewBox="0 0 24 24" aria-hidden="true"><path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="m21 21-6-6m2-5a7 7 0 1 1-14 0 7 7 0 0 1 14 0"></path></svg><input style="padding:0 .5rem 0 3rem !important;width:100%;appearance:none !important;background-color:#f5f5f5;border:none;border-radius:0.75rem;box-shadow:0px -1px 2px rgba(17, 24, 39, .15);color:#{base_color};outline:2px solid transparent !important;min-height:2.25rem !important;font-size:.875rem;line-height:1.25rem;margin:0;padding-block:1px;padding-inline:2px;overflow-clip-margin:0px !important;overflow:clip !important;box-sizing:border-box;" id="{input_id}" value="{default_text}" class="!min-h-9 text-sm" type="text" placeholder="Search..." autocomplete="off"></div>{sc}'